function initData(){document.getElementById("overview").value=document.getElementById("editoverview").value;document.getElementById("content").value=CKEDITOR.instances.editor.getData()}function checkData(){for(var t=document.getElementsByClassName("data"),n=0;n<t.length;n++)if(t[n].value.trim()=="")return!1;return!0}function postData(){confirm("确定提交?")&&document.getElementById("submit").click()}function upload(){initData();checkData()?postData():alert("请完善数据")}function viewCover(){var t=document.getElementById("cover").value,n=document.getElementById("coverViewer");t!=""?(n.removeAttribute("hidden"),n.src=t):n.setAttribute("hidden","")}