function submit(){var n=document.getElementById("articleid").value,i=document.getElementById("message").value,r=document.getElementById("verfyInput").value,t={id:n,detail:i,code:r};console.log(t);$.ajax({type:"POST",url:"/Blog/Comment",data:t,success:function(){window.location.href="/Home/Message?msg=评论成功&url=/Blog/Detail?articleID="+n},error:function(n){alert(JSON.parse(n.responseText).detail);document.getElementById("verfyImg").click()}})}