function fullHeight(){var t,n;if(!isMobile.any())for(t=document.getElementsByClassName("js-fullheight"),n=0;n<t.length;n++)t[n].style.height=window.innerHeight+"px"}function MoveTop(){window.scrollTo(0,0)}function getVerfyCode(n){n.src="/Service/VerificationCode?"+Math.random()}function init(){window.onscroll=()=>{window.scrollY>200?document.querySelector(".js-top").classList.add("active"):document.querySelector(".js-top").classList.remove("active")};window.onresize=fullHeight;fullHeight()}var isMobile={Android:function(){return navigator.userAgent.match(/Android/i)},BlackBerry:function(){return navigator.userAgent.match(/BlackBerry/i)},iOS:function(){return navigator.userAgent.match(/iPhone|iPad|iPod/i)},Opera:function(){return navigator.userAgent.match(/Opera Mini/i)},Windows:function(){return navigator.userAgent.match(/IEMobile/i)},any:function(){return isMobile.Android()||isMobile.BlackBerry()||isMobile.iOS()||isMobile.Opera()||isMobile.Windows()}};init();