function guid(){return"xxxxxxxx-xxxx-4xxx-yxxx-xxxxxxxxxxxx".replace(/[xy]/g,function(n){var t=Math.random()*16|0,i=n=="x"?t:t&3|8;return i.toString(16)})}function render(n){editormd.markdownToHTML(n,{htmlDecode:"style,script,iframe",emoji:!0,taskList:!0,flowchart:!0,sequenceDiagram:!0})}function init(){document.querySelectorAll(".markdown").forEach(n=>{var t=guid();n.setAttribute("id",t);render(t)})}